import React, { Component } from "react";
import axios from "axios";
import "./login.css";


export default class Home extends Component {
    constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.state = {
      username: "",
      password: "",
    }}

      componentDidMount() {
    this.setState({
      username: "",
      password: "",
    })}


    onChangeUsername(e) {
          this.setState({
            title1: e.target.value,
          });
        }

      onChangePassword(e) {
            this.setState({
              title1: e.target.value,
            });
          }


          onSubmit = (e) => {
                e.preventDefault();
                const admin = {
                  username: this.state.username,
                  password: this.state.password,
                }

                console.log(admin);
                    axios
                      .put(
                        "http://localhost:5004/login",
                        admin
                      )
                      .then((res) => console.log(res.data));
                    // window.location =
                  };

  render() {
    return (
      <div>
       <div class="wrapper fadeInDown">
  <div id="formContent">

    <h2 class="active"> Sign In </h2>

    <form onSubmit={this.onSubmit}>
      <input type="text2" id="login" class="fadeIn second" name="login" placeholder="login" value={this.state.username} onChange={this.o}/>
      <input type="text2" id="password" class="fadeIn third" name="login" placeholder="password"/>
      <a href="/dashboard"><input type="button2" class="fadeIn fourth" value="Log In"/></a>
    </form>


    <div id="formFooter">
      <a class="underlineHover" href="#">Forgot Password?</a>
    </div>

  </div>
</div>
      </div>
    );
  }
}
