import React, { Component } from 'react';
import axios from 'axios';
export default class About extends Component{
    constructor(props){
        super(props);

        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.state = {
            title: '',
            description: ''
        }
    }

    componentDidMount(){
        this.setState({
            title: 'title',
            description: 'description'
        })
    }


    onChangeTitle1(e) {


        this.setState({
            title1:  e.target.value,


        })
    }

    onChangeDescription1(e) {



        this.setState({    
            description1: e.target.value,


        })
    }

    onChangeTitle2(e) {


        this.setState({
            title2:  e.target.value,


        })
    }

    onChangeDescription2(e) {



        this.setState({    
            description2: e.target.value,


        })
    }
    onChangeTitle3(e) {


        this.setState({
            title3:  e.target.value,


        })
    }

    onChangeDescription3(e) {



        this.setState({    
            description3: e.target.value,


        })
    }

    onChangeTitle4(e) {


        this.setState({
            title4:  e.target.value,


        })
    }

    onChangeDescription4(e) {



        this.setState({    
            description4: e.target.value,


        })
    }

    onSubmit=(e)=>{
        e.preventDefault();
        const exercise = {
            title1: this.state.title,
            description1:  this.state.description,
            title2: this.state.title,
            description2:  this.state.description,
            title3: this.state.title,
            description3:  this.state.description,
            title4: this.state.title,
            description4:  this.state.description
        }

        console.log(exercise);
        axios.put('http://localhost:5001/update/about/60e49e6cafd56a2a8a728039', exercise)
        .then(res => console.log(res.data));
        // window.location = '/';

    }


render(){
    return(
        <div>
            <h3>Creat new data</h3>
            <form onSubmit={this.onSubmit}>
                <div>
                    <label>Title</label>
                    <input type="text" 
                        required
                        value={this.state.title1}
                        onChange={this.onChangeTitle1}
                        />


                </div>
                <div>
                    <label>Description</label>
                    <input type="text" 
                        required
                        value={this.state.description1}
                        onChange={this.onChangeDescription1}
                        />


                </div>
                <div>
                    <label>Title</label>
                    <input type="text" 
                        required
                        value={this.state.title2}
                        onChange={this.onChangeTitle2}
                        />


                </div>
                <div>
                    <label>Description</label>
                    <input type="text" 
                        required
                        value={this.state.description2}
                        onChange={this.onChangeDescription2}
                        />


                </div>
                <div>
                    <label>Title</label>
                    <input type="text" 
                        required
                        value={this.state.title1}
                        onChange={this.onChangeTitle3}
                        />


                </div>
                <div>
                    <label>Description</label>
                    <input type="text" 
                        required
                        value={this.state.description1}
                        onChange={this.onChangeDescription3}
                        />


                </div>
                <div>
                    <label>Title</label>
                    <input type="text" 
                        required
                        value={this.state.title1}
                        onChange={this.onChangeTitle14}
                        />


                </div>
                <div>
                    <label>Description</label>
                    <input type="text" 
                        required
                        value={this.state.description1}
                        onChange={this.onChangeDescription1}
                        />


                </div>
                <div>
                    <input type="submit" value="Create About data"/>
                </div>
            </form>
        </div>

    )}
}
