import React, { Component } from 'react';
import './home.scss';


const images = document.querySelectorAll(".slide"),
  next = document.querySelector(".next"),
  prev = document.querySelector(".prev");

let current = 0;

function changeImage() {
  images.forEach(img => {
    img.classList.remove("show");
    img.style.display = "none";
  });

  images[current].classList.add("show");
  images[current].style.display = "block";
}

// Calling first time
changeImage();

next.addEventListener("click", function() {
  current++;

  if (current > images.length - 1) {
    current = 0;
  } else if (current < 0) {
    current = images.length - 1;
  }

  changeImage();
});
prev.addEventListener("click", function() {
  current--;

  if (current > images.length - 1) {
    current = 0;
  } else if (current < 0) {
    current = images.length - 1;
  }

  changeImage();
});

// Auto change in 5 seconds

setInterval(() => {
  next.click();
}, 5000);

export default class Home extends Component{
render(){   
    return(
        <div>


          <div class="slider">
      <div class="slide">
        <img src="https://picsum.photos/800?t=1
" />
        <div class="caption">Caption First</div>
      </div>
      <div class="slide">
        <img src="https://picsum.photos/800?t=2
" />
        <div class="caption">Caption Second</div>
      </div>
      <div class="slide">
        <img src="https://picsum.photos/800?t=3
" />
        <div class="caption">Caption Third</div>
      </div>
  <div class="slide">
        <img src="https://picsum.photos/800?t=4
" />
        <div class="caption">Caption Fourth</div>
      </div>
  <div class="slide">
        <img src="https://picsum.photos/800?t=5
" />
        <div class="caption">Caption Fifth</div>
      </div>


      <a class="prev">&#10094;</a>
      <a class="next">&#10095;</a>
    </div>
        </div>
    )}
}
