import React, { Component } from "react";
import axios from "axios";
import "./about.css";
import Footer from "../../components/footer/footer";



export default class Contacts extends Component {
  constructor(props) {
    super(props);

    // this.deleteAbout = this.deleteAbout.bind(this)

    this.state = { About: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5003/about/")
      .then((response) => {
        this.setState({ About: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  // deleteAbout(id) {
  //     axios.delete('http://localhost:5000/about/'+id)
  //       .then(response => { console.log(response.data)});

  //     this.setState({
  //       About: this.state.About.filter(el => el._id !== id)
  //     })
  //   }

  render() {
    return (
      <div>
        {/* <h1>{this.state.About && this.state.About[0] && this.state.About[1].title}</h1> */}

        {this.state.About &&
          this.state.About.map((item, index) => {
            return (
              <div className="container1">
                <div className="data">
                  <div className="title" key={item._id}>
                  <div className="button1 ">
                    <a href='../components/pdf/Hassan.resume.pdf' download>Resume</a>
                    </div>
                    <div class="back container">
    <h1>My Pop up window example</h1>
    <button><a href="#">Click me</a></button>
</div> 

<div class="pop-window">
    <div class="inner">
        <h2>This info is from a pop up</h2>
        <p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. 
           Donec odio. Quisque volutpat mattis eros. 
           Nullam malesuada erat ut turpis. Suspendisse urna nibh, 
            viverra non, semper suscipit, posuere a, pede.
        </p>
    </div>
</div>
                    <h1>{item.title1}</h1>
                    <p>{item.description1}</p>
                    <h1>{item.title2}</h1>
                    <p>{item.description2}</p>
                    <h1>{item.title3}</h1>
                    <p>{item.description3}</p>
                    <h1>{item.title4}</h1>
                    <p>{item.description4}</p>
                  </div>
                </div>
                <div className="personal_img">
                  <img src="https://dreambuilders.dk/wp-content/uploads/2015/03/myAvatar-1.png" />
                </div>
              </div>
            );
          })}
        <Footer />
      </div>
    );
  }
}
