import React, { Component } from 'react';
import axios from 'axios';
export default class About extends Component{
    constructor(props){
        super(props);

        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.state = {
            title: '',
            description: ''
        }
    }

    componentDidMount(){
        this.setState({
            title: 'title',
            description: 'description'
        })
    }


    onChangeTitle(e) {


        this.setState({
            title:  e.target.value,
            title1: e.target.value,
            title2: e.target.value,
            title3: e.target.value

        })
    }

    onChangeDescription(e) {



        this.setState({    
            description1: e.target.value,
            description2: e.target.value,
            description3: e.target.value,
            description4: e.target.value,

        })
    }

    onSubmit=(e)=>{
        e.preventDefault();
        const exercise = {
            title1: this.state.title,
            description1:  this.state.description,
            title2: this.state.title,
            description2:  this.state.description,
            title3: this.state.title,
            description3:  this.state.description,
            title4: this.state.title,
            description4:  this.state.description
        }

        console.log(exercise);
        axios.put('http://localhost:5001/update/about/60e49e6cafd56a2a8a728039', exercise)
        .then(res => console.log(res.data));
        // window.location = '/';

    }


render(){
    return(
        <div>
            <h3>Creat new data</h3>
            <form onSubmit={this.onSubmit}>
                <div>
                    <label>Title</label>
                    <input type="text" 
                        required
                        value={this.state.title}
                        onChange={this.onChangeTitle}
                        />


                </div>
                <div>
                    <label>Description</label>
                    <input type="text" 
                        required
                        value={this.state.description}
                        onChange={this.onChangeDescription}
                        />


                </div>
                <div>
                    <input type="submit" value="Create About data"/>
                </div>
            </form>
        </div>

    )}
}
