import React, { Component } from 'react';
import './contact.css';
import axios from "axios";
import FacebookIcon from '@material-ui/icons/Facebook';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import WhatsAppIcon from '@material-ui/icons/WhatsApp';
import GitHubIcon from '@material-ui/icons/GitHub';
import TelegramIcon from '@material-ui/icons/Telegram';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import MailIcon from '@material-ui/icons/Mail';
import PhoneIcon from '@material-ui/icons/Phone';
import { Link } from "react-router-dom";
import Navbar from "../../components/navbar/navbar";
export default class Mailer extends Component{
  constructor(props) {
    super(props);

    // this.deleteAbout = this.deleteAbout.bind(this)

    this.state = { Contact: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5001/contact/")
      .then((response) => {
        this.setState({ Contact: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <div>
        <Navbar />
          {this.state.Contact &&
          this.state.Contact.map((item, index) => {
            return (

        <section id="contact">

        <div class="contact-wrapper">



          <form id="contact-form" class="form-horizontal" role="form">

            <div class="form-group">
              <div class="col-sm-12">
                <input type="text" class="form-control" id="name" placeholder="NAME" name="name"  required />
              </div>
            </div>

            <div class="form-group">
              <div class="col-sm-12">
                <input type="email" class="form-control" id="email" placeholder="EMAIL" name="email"  required />
                <textarea class="form-control" rows="10" placeholder="MESSAGE" name="message" required></textarea>
              </div>
            </div>           

            <button class="btn btn-primary send-button" id="submit" type="submit" value="SEND"  >
              <div class="alt-send-button">
                <i class="fa fa-paper-plane"></i><span class="send-text"> SEND<TelegramIcon/></span>
              </div>

            </button>

          </form>



            <div class="direct-contact-container">

              <ul class="contact-list">
                <li class="list-item"><i class="fa fa-map-marker fa-2x"><span class="contact-text place"><LocationOnIcon /><a href="https://www.google.com.lb/maps/place/Chouaifet+El+Aamroussieh/@33.8100534,35.5107895,14.88z/data=!4m5!3m4!1s0x151f19aafb7cf2d7:0xcf88762c7c287baa!8m2!3d33.8095311!4d35.5198113?hl=en" target="_blank">Lebanon,Choueifat</a></span></i></li>

                <li class="list-item"><i class="fa fa-phone fa-2x"><span class="contact-text phone"><a href={item.Phone} title="Give me a call" target="_blank"><PhoneIcon />{item.Phone}</a></span></i></li>

                <li class="list-item"><i class="fa fa-envelope fa-2x"><span class="contact-text gmail"><a href="#" title="Send me an email"><MailIcon />{item.Email}</a></span></i></li>

              </ul>


              <div class="social-media-list">

              <Link to={{ pathname: "https://" + item.Github}}  target="_blank">
                  <i aria-hidden="true" ><GitHubIcon/></i></Link>

                <Link to={{ pathname: "https://" + item.Linkedin}} target="_blank">
                  <i aria-hidden="true"></i><LinkedInIcon/></Link>

                <Link to={{pathname: "https://"+ item.Facebook}} target="_blank" className="social-media-list">
                  <i aria-hidden="true"></i><FacebookIcon/></Link>

                <Link to= {{pathname: "https://" + item.Whatsapp}} target="_blank">
                  <i aria-hidden="true"></i><WhatsAppIcon/></Link>

              </div>

              <div class="copyright">&copy; BY MAHER ALIWE</div>

            </div>


        </div>



      </section>  
            );
          })}
          </div>
    );
  }
}
