import React, { Component } from 'react';
import axios from 'axios';
export default class Contacts extends Component{
    constructor(props){
        super(props);

        this.deleteAbout = this.deleteAbout.bind(this)

        this.state = {About: []};
    }

    componentDidMount(){
        axios.get('http://localhost:5001/about/')
        .then(response =>{
            this.setState({About: response.data})

        })
        .catch((error) => {
            console.log(error);
        })
    }

    deleteAbout(id) {
        axios.delete('http://localhost:5001/about/'+id)
          .then(response => { console.log(response.data)});

        this.setState({
          About: this.state.About.filter(el => el._id !== id)
        })
      }


render(){


    return(
        <div>
        {/* <h1>{this.state.About && this.state.About[0] && this.state.About[1].title}</h1> */}

        {this.state.About && this.state.About.map((item,index)=>{
                return (
                    <div style={{backgroundColor: "yellow", marginBottom: 20}} key={item._id}>
                <h1>{item.title}</h1>
                <p>{item.description}</p>
                </div>

                    )
        })}
      </div>
    )}
}
