import React, { Component } from "react";
import "./experience .css";
import axios from "axios";
import Navbar from "../../components/navbar/navbar";

export default class Experience extends Component {
  constructor(props) {
    super(props);

    // this.deleteProjects = this.deleteProjects.bind(this)

    this.state = { Projects: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5003/project/")
      .then((response) => {
        this.setState({ Projects: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }
  render() {
    return (
      <div>
        <Navbar />
        <div>
        {this.state.Projects &&
            this.state.Projects.map((item, index) => {
              return (<div>
        <div class="page" key={item._id}>
  <div class="page__container">
    <a href="#popup-article" class="open-popup">{item.title}</a>
  </div>
</div>
<div id="popup-article" class="popup">
  <div class="popup__container">
    <a href="#" class="popup__close">
      <span class="screen-reader">close</span>
    </a>  
    <div class="popup__content">
      <h1 class="popup__title r-title">Description</h1>
     {item.description}
    </div>
  </div>
</div>

<button type="button" class="popup-btn">Show Popup</button>
        </div>

    );
})}</div></div>);
  }
}
