  constructor(props) {
    super(props);

    // this.deleteAbout = this.deleteAbout.bind(this)

    this.state = { About: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5003/about/")
      .then((response) => {
        this.setState({ About: response.data });
