import React, { Component } from "react";
import axios from "axios";

// import ProjectCard from "../../components/Projects-cards/project.cards";
// const project = [
//   { title: "hello", desc: "project1" },
//   { title: "hello2", desc: "project2" },
//   { title: "hello3", desc: "project3" },
//   { title: "hello", desc: "project1" },

// ];

export default class Projects extends Component {
  constructor(props) {
    super(props);

    // this.deleteProjects = this.deleteProjects.bind(this)

    // this.state = { Projects: [] };
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);

    this.state = {
      title: '',
      description: '',

    }
  }



   componentDidMount() {
     axios
      .get("http://localhost:5003/project/")
      .then((response) => {
        this.setState({ 
          title: response.data.title,
          description: response.data.description,
         });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  onChangeTitle(e) {
    this.setState({
      title: e.target.value
    })
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const exercise = {
      username: this.state.username,
      description: this.state.description,

    }

    console.log(exercise);

    axios.post('http://localhost:5000/exercises/update/' + this.props.match.params.id, exercise)
      .then(res => console.log(res.data));

    window.location = '/';
  }


  // updateProjects = (id) => {
  //   console.log();
  //   axios.put(`http://localhost:5003/project/update/${id}`).then((response) => {
  //     console.log(response.data);
  //   });
  // };

  render() {
    return (
      <div>
        <div className="cards6">
          {this.state.Projects &&
            this.state.Projects.map((item, index) => {
              return (
                <div
                  class="container7"
                  style={{ margin: "20px" }}
                  key={item._id}
                >
                  Title <br></br>
                  <input type="text" value={item.title} name="title" />
                  <br></br>
                  Description{" "}
                  <input
                    type="text"
                    value={item.description}
                    name="description"
                  />
                  <img
                    src={`http://localhost:5003/project/projects/uploads/${item.image}`}
                    alt=""
                    height="200px"
                    className="card8-img"
                  ></img>
                  <label>Upload image</label>
                  <input type="file" name="image" />
                  <br></br> <br></br>{" "}
                  <button
                    type="submit"
                    style={{ marginLeft: "40%" }}
                    onClick={() => this.updateProjects(item._id)}
                  >
                    update
                  </button>
                </div>
              );
            })}
        </div>
      </div>
    );
  }
}
