import React, { Component } from "react";

import axios from "axios";

// import ProjectCard from "../../components/Projects-cards/project.cards";
// const project = [
//   { title: "hello", desc: "project1" },
//   { title: "hello2", desc: "project2" },
//   { title: "hello3", desc: "project3" },
//   { title: "hello", desc: "project1" },

// ];

export default class Projects extends Component {
  constructor(props) {
    super(props);

    // this.deleteProjects = this.deleteProjects.bind(this)

    this.state = { Projects: [] };
  }




  async componentDidMount() {
    await axios
      .get("http://localhost:5003/project/")
      .then((response) => {
        this.setState({ Projects: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  updateProjects = (id)=> {
      console.log()
      axios.delete(`http://localhost:5003/project/update/${id}`)
        .then(response => { console.log(response.data)});

      this.setState({
        Projects: this.state.Projects.filter(el => el._id !== id)
      })
    }

  render() {
    return (
      <div>

        <div className="cards">
          {this.state.Projects &&
            this.state.Projects.map((item, index) => {
              return (
                <div class="container5" key={item._id}>
                  <div className="card">


                  </div>
                  <button type="submit" style={{marginLeft:'40%'}} onClick={(()=>this.updateProjects(item._id))}>update</button>
                </div>
              );
            })}
        </div>

      </div>
    );
  }
}
