import React, { Component } from 'react';
import axios from 'axios';
export default class About extends Component{
    constructor(props){
        super(props);

        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);



        this.state = {
            title: '',
            description: ''
        }
    }
    componentDidMount() {
        axios.get('http://localhost:5001/about/add/')
          .then(response => {
            if (response.data.length > 0) {
              this.setState({
                title: response.data.map(exercise => exercise.title),
                title: response.data[0].tit
              })
            }
          })
          .catch((error) => {
            console.log(error);
          })

      }
    // componentDidMount(){
    //     this.setState({
    //         title: 'title',
    //         description: 'description'
    //     })
    // }


    onChangeTitle(e) {


        this.setState({
            title: e.target.value
        })
    }

    onChangeDescription(e) {



        this.setState({    
            description: e.target.value
        })
    }

    onSubmit=(e)=>{
        e.preventDefault();
        const exercise = {
            title: this.state.title,
            description: this.state.description
        }

        console.log(exercise);
        axios.post('http://localhost:5001/about/add', exercise)
        .then(res => console.log(res.data));
        // window.location = '/';

    }


render(){
    return(
        <div>
            <h3>Creat new data</h3>
            <form onSubmit={this.onSubmit}>
                <div>
                    <label>Title</label>
                    <input type="text" 
                        required
                        value={this.state.title}
                        onChange={this.onChangeTitle}
                        />


                </div>
                <div>
                    <label>Description</label>
                    <input type="text" 
                        required
                        value={this.state.description}
                        onChange={this.onChangeDescription}
                        />


                </div>
                <div>
                    <input type="submit" value="Create About data"/>
                </div>
            </form>
        </div>

    )}
}
