import React, { Component } from 'react';
import axios from 'axios';
export default class Contacts extends Component{
    constructor(props){
        super(props);

        this.deleteAbout = this.deleteAbout.bind(this)

        this.state = {About: []};
    }

    componentDidMount(){
        axios.get('http://localhost:5001/about/')
        .then(response =>{
            console.log(response);
            this.setState({About: response.data})

        })
        .catch((error) => {
            console.log(error);
        })
    }

    // aboutList() {
    //     return this.state.About.map(currentabout => {
    //       return <Exercise About={currentabout} deleteAbout={this.deleteAbout} key={currentabout._id}/>;
    //     })
    //   }

    deleteAbout(id) {
        axios.delete('http://localhost:5001/about/'+id)
          .then(response => { console.log(response.data)});

        this.setState({
          About: this.state.About.filter(el => el._id !== id)
        })
      }


render(){
    return(
        <div>
        <h3>Logged Exercises</h3>

        <h1>{this.state.About[0].data.title}</h1>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Username</th>
              <th>Description</th>
              <th>Duration</th>
              <th>Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {/* { this.aboutList() } */}
          </tbody>
        </table>
      </div>
    )}
}
