import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Projects = props => (
  <tr>
    <td>{props.projects.title}</td>
    <td>{props.projects.description}</td>

    <td>
      <Link to={"/project/update/"+props.projects._id}>edit</Link> | <a href="#" onClick={() => { props.deleteProject(props.projects._id) }}>delete</a>
    </td>
  </tr>
)

export default class ProjectsList extends Component {
  constructor(props) {
    super(props);

    this.deleteProject = this.deleteProject.bind(this)

    this.state = {Project: []};
  }

  componentDidMount() {
    axios.get('http://localhost:5003/project/')
      .then(response => {
        this.setState({ Project: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  deleteProject(id) {
    axios.delete('http://localhost:5003/project/delete/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      Project: this.state.projects.filter(el => el._id !== id)
    })
  }

  projectList() {
    return this.state.Project.map(currentprojects => {
      return <Projects projects={currentprojects} deleteProject={this.deleteProject} key={currentprojects._id}/>;
    })
  }

  render() {
    return (
      <div>
        <h3>Logged project</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Title</th>
              <th>Description</th>

            </tr>
          </thead>
          <tbody>
            { this.projectList() }
          </tbody>
        </table>
      </div>
    )
  }
}
